sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
	var whatneeded=g_form.getValue('u_string_2');
	g_form.clearOptions('u_string_2');

	if(newValue=='facilities'){
		g_form.addOption('u_string_2','f1','F1');
		g_form.addOption('u_string_2','f2','F2');
		g_form.addOption('u_string_2','other','Other');

	}
	if(isLoading && !g_form.isNewRecord())
	g_form.setValue('u_string_2', whatneeded);

   if(newValue=='facilities'){
	   g_form.setValue('u_string_2', 'facilities', 'Facilities');
   }
	if(newValue=='legal'){
		 g_form.setValue('u_string_2', 'legal', 'Legal');
	}
	if(newValue=='human resources'){
		 g_form.setValue('u_string_2', 'human resources', 'Human Resources');
	}
	if(newValue=='--none--'){
		g_form.clearOptions('u_string_2');
	}
}
